mapper.select:
SELECT id, UPPER(type), score, date, numRounds FROM darts_result
WHERE type = #{type}
AND darts_user = #{username}
ORDER BY date DESC;
-- to filter by date:  AND date >= STR_TO_DATE('2012-12-16 10:00:00', '%Y-%m-%d %H:%i:%s');

SELECT  id, UPPER(type), score, date, numRounds  FROM darts_result
WHERE type = #{type}
AND darts_user = #{username}
ORDER BY date DESC
LIMIT 10;

SELECT  COUNT(1)  FROM darts_result
WHERE type = #{type}
AND darts_user = #{username};
